<style>
 
 /* help-support.component.scss */

:host {
  display: block;
  margin-top: 20px;
}

:host .chatbot-container {
  width: 400px;
  height: 500px;
  border: 1px solid #ccc;
  overflow-y: scroll;
  padding: 10px;
}

:host .input-container {
  margin-top: 10px;
}

:host .input-container input {
  width: 100%;
  padding: 5px;
  font-size: 14px;
}

:host .input-container button {
  margin-top: 10px;
  padding: 5px 10px;
  font-size: 14px;
  background-color: #007bff;
  color: #fff;
  border: none;
  cursor: pointer;
}

:host .message {
  margin-bottom: 10px;
}

:host .user-message {
  text-align: right;
}

:host .bot-message {
  text-align: left;
}

:host .timestamp {
  font-size: 12px;
  color: #888;
}



  

  
  

</style>



